  'https://ng-course-recipe-book-a8fae-default-rtdb.firebaseio.com';
enum errorMessages {
  EMAIL_EXISTS = 'The email address is already in use by another account',
  OPERATION_NOT_ALLOWED = 'Password sign-in is disabled for this project',
